/**
 * VAST Client Proxy for CustomTube
 * This is a minimal implementation to prevent errors in the ads.js system
 * In a production environment, you would include the full VAST client library
 */
(function(window) {
    'use strict';

    var VAST = {
        VASTClient: function() {
            this.get = function(url) {
                return {
                    // Return a minimal promise implementation that simply resolves with an empty ads array
                    then: function(callback) {
                        setTimeout(function() {
                            callback({
                                ads: []
                            });
                        }, 100);
                        return {
                            catch: function(errorCallback) {
                                // Provide error handling chain
                                return this;
                            }
                        };
                    }
                };
            };
        }
    };

    // Expose to global scope
    window.VAST = VAST;
})(window);